import React from "react";
import styles from './Block2.module.scss'

const Text = (props) => {
    if(props.tmp === "0") {
        return(
            <p className={styles.Block2__TextColom__TextBig}>{props.text1}<br />{props.text2}</p>
        )
    } else if (props.tmp === "1") {
        return(
            <p className={styles.Block2__TextColom__TextSmall}>{props.text1}<br />{props.text2}</p>
        )
    }
}

export {Text}
