import React from "react";
import styles from './Block2.module.scss'
import { Text } from "./Text";
import pic from '../img/pana.png'
const SecondBlock = () => {
    return(
        <div className={styles.Block2}>
            <div className={styles.Block2__TextColom}>
                <Text text1="Welcome to Learn" text2="Marketing!" tmp="0" />
                <Text text1="Are you ready to boost your" text2="marketing skills!?" tmp="1" />
                <button className={styles.Block2__TextColom__Btn}>
                    Buy now
                </button>
            </div>
            <div className={styles.Block2__PicBlock}>
                <img src={pic} alt="" className={styles.Block2__PicBlock__Img} />
            </div>
        </div>
    )
}

export {SecondBlock}
