import React, { useContext, useEffect, useState } from "react";
import styles from './Header.module.scss'
import { Name } from "./Name";
import { HeaderButton } from "./HeaderButton";
import pic2 from '../img/burgerBlack.svg'

const Header = (props) => {
    const changeNavbar = () => {
        props.setStyle(styles.Header__Show); 
        props.setPic(pic2); 
        console.log(props.pic);
        console.log(props.style);
    }

    return (
        <div className={styles.Header}>
            <Name />
            <div className={[styles.Header__ButtonsBlock, props.style].join(' ')} onClick={e => e.stopPropagation()}>
                <HeaderButton func={props.func} tmp={1}  text="About us" />
                <HeaderButton func={props.func} tmp={2} text="Course" />
                <HeaderButton func={props.func} tmp={3} text="Pricing plans" />
                <HeaderButton func={props.func} tmp={4} text="Feedbacks" />
                <HeaderButton func={props.func} tmp={5}  text="Contact us" />
            </div>
            <div className={styles.Header__Burger} onClick={() => {props.setStyle(styles.Header__Show); props.setPic(pic2); console.log(props.pic)}}>
                <img src={props.pic} alt="" className={styles.Header__Burger__Pic}/>
            </div>
        </div>
    )
}

export {Header}
