import React, { useContext, useEffect } from "react";
import styles from './Header.module.scss'
import { Context1, Context2, Context3, Context4, Context5 } from "../Page";

const HeaderButton = (props) => {
    let elem
    useEffect(() => {
        switch (props.tmp) {
            case 1: elem = useContext(Context1)
                break;
            case 2: elem = useContext(Context2)
                break;
            case 3: elem = useContext(Context3)
                break;
            case 4: elem = useContext(Context4)
                break;
            case 5: elem = useContext(Context5)
                break;
            default:
                break;
        }
    }, [])
    const Scroll = () => {
        elem.current.ScrollIntoView({ behavior: 'smooth'})
    }
    return(
        <p className={styles.Header__ButtonsBlock__Text} onClick={scroll}>
            {props.text}
        </p>
    )
}

export {HeaderButton}
